import { Component, OnInit } from '@angular/core';
import { CartService } from '../cart.service';
import { FoodItem } from '../food-item.model';

@Component({
  selector: 'app-food-list',
  templateUrl: './food-list.component.html',
  styleUrls: ['./food-list.component.css']
})
export class FoodListComponent implements OnInit {
  constructor(private service: CartService) { }
  foodItems: FoodItem[] = [];
  quantities: number[] = [];
  message='';
  ngOnInit(): void {
    this.getFoodItems();
  }

  getFoodItems(): void {
    this.service.getFoodItems().subscribe(foodItems => {
      this.foodItems = foodItems;
      this.initializeQuantities();
    });
  }
  initializeQuantities(): void {
    this.quantities = new Array(this.foodItems.length).fill(1);
  }
  addToCart(foodItem: FoodItem, quantity: number): void {
    if (quantity <= 0) {
      alert('Please select a valid quantity');
    }
    else if (quantity <= foodItem.availableQuantity) {
      this.service.addToCart(foodItem.foodItemId, quantity).subscribe(
        response=>this.message=response
      );
        console.log(this.message);

    } else {
      alert('Quantity exceeds available quantity');
    }
  }
}
<div *ngIf="message!=''">{{message}}</div>

<table class="table table-hover table-primary table-striped">
  <thead>
    <tr>
      <th>Name</th>
      <th>Description</th>
      <th>Price</th>
      <th>Category</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let foodItem of foodItems; let i = index">
      <td>{{ foodItem.name }}</td>
      <td>{{ foodItem.description }}</td>
      <td>{{ foodItem.price | currency : "INR" }}</td>
      <td>{{ foodItem.category.name }}</td>
      <td>
        <input type="number" [(ngModel)]="quantities[i]" [min]="1" />
        <button (click)="addToCart(foodItem, quantities[i])">
          Add to Cart
        </button>
      </td>
    </tr>
  </tbody>
</table>
