import { Component, OnInit } from '@angular/core';
import { FoodBoxService } from '../food-box.service';

@Component({
  selector: 'app-contact',
  templateUrl: './contactus.component.html',
  styleUrls: ['./contactus.component.css']
})
export class ContactusComponent implements OnInit {
  formData = {
    name: '',
    email: '',
    subject: '',
    message: ''
  };

  constructor(private service: FoodBoxService) {}

  ngOnInit(): void {}

  submitForm(): void {
    if (this.validateForm()) {
      // Call your service method to send the form data to the backend
      // this.service.sendContactForm(this.formData).subscribe(
      //   response => {
      //     console.log('Form submission successful:', response);
      //     // Clear form data after successful submission
      //     this.clearForm();
      //   },
      //   error => {
      //     console.error('Form submission error:', error);
      //   }
      // );
    }
  }

  validateForm(): boolean {
    // Implement your validation logic here
    // For example, check if all required fields are filled
    // You can also show validation messages to the user
    return true; // Return true if form is valid, false otherwise
  }

  clearForm(): void {
    // Clear form data after successful submission
    this.formData = {
      name: '',
      email: '',
      subject: '',
      message: ''
    };
  }
}
<div class="container-xxl py-5">
    <div class="container">
        <div class="text-center wow fadeInUp" data-wow-delay="0.1s">
            <h5 class="section-title ff-secondary text-center text-primary fw-normal">Contact Us</h5>
            <h1 class="mb-5">Any Query? Feel free to drop a message</h1>
        </div>
        <div class="row g-4">
            <div class="col-12">
                <div class="row gy-4">
                    <div class="col-md-4">
                        <h5 class="section-title ff-secondary fw-normal text-start text-primary">Booking</h5>
                        <p><i class="fa fa-envelope-open text-primary me-2"></i>book@foodbox.com</p>
                    </div>
                    <div class="col-md-4">
                        <h5 class="section-title ff-secondary fw-normal text-start text-primary">General</h5>
                        <p><i class="fa fa-envelope-open text-primary me-2"></i>info@foodbox.com</p>
                    </div>
                    <div class="col-md-4">
                        <h5 class="section-title ff-secondary fw-normal text-start text-primary">Technical</h5>
                        <p><i class="fa fa-envelope-open text-primary me-2"></i>tech@foodbox.com</p>
                    </div>
                </div>
            </div>
            <div class="col-md-6 wow fadeIn" data-wow-delay="0.1s">
                <iframe class="position-relative rounded w-100 h-100"
                    src="https://maps.google.com/maps?q=L 147, 5th Main Rd, Sector 6, HSR Layout, Bengaluru, Karnataka 560102&t=&z=12&ie=UTF8&iwloc=&output=embed"
                    frameborder="0" style="min-height: 350px; border:0;" allowfullscreen="" aria-hidden="false"
                    tabindex="0"></iframe>
            </div>
            <div class="col-md-6">
                <div class="wow fadeInUp" data-wow-delay="0.2s">
                    <form (ngSubmit)="submitForm()">
                        <div class="row g-3">
                            <div class="col-md-6">
                                <div class="form-floating">
                                    <input type="text" class="form-control" id="name" placeholder="Your Name"
                                        [(ngModel)]="formData.name" name="name" required>
                                    <label for="name">Your Name</label>
                                </div>
                            </div>
                            <div class="col-md-6">
                                <div class="form-floating">
                                    <input type="email" class="form-control" id="email" placeholder="Your Email"
                                        [(ngModel)]="formData.email" name="email" required>
                                    <label for="email">Your Email</label>
                                </div>
                            </div>
                            <div class="col-12">
                                <div class="form-floating">
                                    <input type="text" class="form-control" id="subject" placeholder="Subject"
                                        [(ngModel)]="formData.subject" name="subject" required>
                                    <label for="subject">Subject</label>
                                </div>
                            </div>
                            <div class="col-12">
                                <div class="form-floating">
                                    <textarea class="form-control" placeholder="Leave a message here" id="message"
                                        style="height: 150px" [(ngModel)]="formData.message" name="message" required></textarea>
                                    <label for="message">Message</label>
                                </div>
                            </div>
                            <div class="col-12">
                                <button class="btn btn-primary w-100 py-3" type="submit">Send Message</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
