import { Component, OnInit } from '@angular/core';
import { FoodBoxService } from '../food-box.service';
import { Router } from '@angular/router';
import { Category } from '../model-classes/category.model';

@Component({
  selector: 'app-category-management',
  templateUrl: './category-management.component.html',
  styleUrls: ['./category-management.component.css']
})
export class CategoryManagementComponent implements OnInit{
  categories: Category[]=[];
  newCategoryName='';
  constructor(private service:FoodBoxService, private router:Router){}

  ngOnInit(): void {
    this.getCategories();
  }
  getCategories():void{
    this.service.getCategories().subscribe(
      categories=>{
        this.categories=categories;
        console.log(categories);

      }
    )
  }
  addCategory(newCategoryName:string):void{
    this.service.addCategory(newCategoryName).subscribe(
      response=>{
        if(response=='0'){
          alert("Category already exists!");
        }
      else{
        this.ngOnInit();
      }
      }
    )
  }
  deleteCategory(categoryId:number):void{
    this.service.deleteCategory(categoryId).subscribe(
      response=>{
        this.ngOnInit();
        }
    )
  }
  manageFoodItems(categoryId:number):void{
    this.router.navigate(['/category', categoryId, 'food-items']);
  }
}
<div class="container">
  <h2 style="color: tomato;">Cuisine Management</h2>
  <div class="row">
    <div class="col-md-6">
      <h4 style="color: #008000;">Add New Cuisine</h4>
      <div class="input-group mb-3">
        <input type="text" class="form-control" [(ngModel)]="newCategoryName" placeholder="Enter cuisine name">
        <button class="btn btn-success" type="button" (click)="addCategory(newCategoryName)">
          <i class="material-icons">add</i> Add
        </button>
      </div>
    </div>
  </div>
  <hr style="border-color: #008000;">
  <h4 style="color: #008000;">Cuisine List</h4>
  <ul class="list-group">
    <li class="list-group-item" *ngFor="let category of categories">
      {{ category.name }}
      <button class="btn btn-danger btn-sm float-end" (click)="deleteCategory(category.categoryId)">
        <i class="material-icons">delete</i> Delete
      </button>
      <button class="btn btn-primary btn-sm float-end" (click)="manageFoodItems(category.categoryId)">
        <i class="material-icons">restaurant_menu</i> Manage Food Items
      </button>
    </li>
  </ul>
</div>
