import { Component, OnInit } from '@angular/core';
import { CartService } from '../cart.service';
import { Category, FoodItem } from '../food-item.model';

@Component({
  selector: 'app-food-list',
  templateUrl: './food-list.component.html',
  styleUrls: ['./food-list.component.css']
})
export class FoodListComponent implements OnInit {
  constructor(private service: CartService) { }
  foodItems: FoodItem[] = [];
  quantities: number[] = [];
  filteredFoodItems: FoodItem[]=[];
  categories: Category[]=[];
  message='';
  ngOnInit(): void {
    this.getCategories();
    this.getFoodItems();
  }
  getCategories() {
    this.service.getCategories().subscribe(categories => {
      this.categories=categories;
    });
  }

  getFoodItems(): void {
    this.service.getFoodItems().subscribe(foodItems => {
      this.foodItems = foodItems;
      this.initializeQuantities();
    });
  }
  initializeQuantities(): void {
    this.quantities = new Array(this.foodItems.length).fill(1);
  }
  addToCart(foodItem: FoodItem, quantity: number): void {
    if (quantity <= 0) {
      alert('Please select a valid quantity');
    }
    else if (quantity <= foodItem.availableQuantity) {
      this.service.addToCart(foodItem.foodItemId, quantity).subscribe(
        response=>this.message=response
      );
        console.log(this.message);

    } else {
      alert('Quantity exceeds available quantity');
    }
  }

increaseQuantity(i:number): void {
  this.quantities[i]++;
}

decreaseQuantity(i:number): void {
  this.quantities[i]--;
}
filterByCategory(categoryId: number): void {
  this.foodItems = this.foodItems.filter(item => item.category.categoryId == categoryId);
}

}
<div *ngIf="message != ''">
  <h2>{{ message }}</h2>
</div>
<div class="categories-tab">
  <ul class="nav flex-column">
    <li class="nav-item" *ngFor="let category of categories">
      <a class="nav-link" (click)="filterByCategory(category.categoryId)">{{ category.name }}</a>
    </li>
  </ul>
</div>
<!-- <div class="search-bar">
  <input type="text" class="form-control" [(ngModel)]="searchQuery" placeholder="Search for food items">
</div> -->

<div class="row">
  <div class="col-md-4 mb-4" *ngFor="let foodItem of foodItems; let i = index">
    <div class="card">
      <img
        src="{{ foodItem.imagePath }}"
        class="card-img-top card-img"
        alt="Food Image"
      />
      <div class="card-body">
        <h3 class="card-title" style="color: tomato;">{{ foodItem.name }}</h3>
        <h4 class="card-title">{{ foodItem.category.name }}</h4>
        <p class="card-text">{{ foodItem.description }}</p>
        <p class="card-text">Price: {{ foodItem.price | currency : "INR" }}</p>
        <p class="card-text">
          Available Quantity: {{ foodItem.availableQuantity }}
        </p>
        <div style="margin: 0 auto; display: flex; align-items: center;">
          <div class="input-group mr-2">
            <div class="input-group-prepend">
              <button
                class="btn btn-outline-warning"
                type="button"
                (click)="decreaseQuantity(i)"
                style="border-radius: 30%;"
              >
                -
              </button>
            </div>
            <input
              type="text"
              class="form-control text-center"
              style="max-width: 50px; border-radius: 30%;"
              [(ngModel)]="quantities[i]"
              readonly
            />
            <div class="input-group-append">
              <button
                class="btn btn-outline-success"
                type="button"
                (click)="increaseQuantity(i)"
                style="border-radius: 30%;"
              >
                +
              </button>
            </div>
          </div>
          <button
            class="btn btn-success add-to-cart-button"
            (click)="addToCart(foodItem, quantities[i])"
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  </div>
</div>
