import { Component, OnInit } from '@angular/core';
import { FoodBoxService } from '../food-box.service';
import { FoodItem } from '../model-classes/food-item.model';
import { Category } from '../model-classes/category.model';

@Component({
  selector: 'app-food-list',
  templateUrl: './food-list.component.html',
  styleUrls: ['./food-list.component.css']
})
export class FoodListComponent implements OnInit {
  constructor(private service: FoodBoxService) { }
  foodItems: FoodItem[] = [];
  quantities: number[] = [];
  filteredFoodItems: FoodItem[] = [];
  categories: Category[] = [];
  selectedCategory: Category | null = null;
  searchQuery = '';
  selectedSortOption: string = 'default';
  alertMessage='';
  ngOnInit(): void {
    this.getCategories();
    this.getFoodItems();
  }
  getCategories() {
    this.service.getCategories().subscribe(categories => {
      this.categories = categories;

    });
  }

  getFoodItems(): void {
    this.service.getFoodItems().subscribe(foodItems => {
      this.foodItems = foodItems;
      this.filteredFoodItems = foodItems;
      this.initializeQuantities();
    });
  }

  initializeQuantities(): void {
    this.quantities = new Array(this.foodItems.length).fill(1);
  }
  addToCart(foodItem: FoodItem, quantity: number): void {
    if (quantity <= 0) {
      this.showAlertModal('Please select a valid quantity');
    }
    else if (quantity <= foodItem.availableQuantity) {
      this.service.addToCart(foodItem.foodItemId, quantity).subscribe(
        response => {
          this.showSuccessModal();}
      );


    } else {
      this.showAlertModal('Quantity exceeds available quantity');
    }
  }
  showSuccessModal(): void {
    const modal = document.getElementById('successModal');
    if (modal) {
      modal.style.display = 'block';
    }
  }
  closeSuccessModal(): void {
    const modal = document.getElementById('successModal');
    if (modal) {
      modal.style.display = 'none';
    }
  }
  showAlertModal(message:string): void {
    const modal = document.getElementById('alertModal');
    this.alertMessage=message;
    if (modal) {
      modal.style.display = 'block';
    }
  }
  closeAlertModal(): void {
    const modal = document.getElementById('alertModal');
    if (modal) {
      modal.style.display = 'none';
    }
  }
  increaseQuantity(i: number): void {
    this.quantities[i]++;
  }

  decreaseQuantity(i: number): void {
    this.quantities[i]--;
  }
  filterByCategory(category: Category | null): void {
    this.selectedCategory = category;
    if (category) {
      this.filteredFoodItems = this.foodItems.filter(
        foodItem => foodItem.category.categoryId === category.categoryId

      );
    } else {
      this.filteredFoodItems = this.foodItems;
    }
  }
  applyFilters(): void {
    this.filterByCategory(this.selectedCategory);
    this.filteredFoodItems = this.filteredFoodItems.filter(item =>
      item.name.toLowerCase().includes(this.searchQuery.toLowerCase())
    );
    if (this.selectedSortOption === 'lowToHigh') {
      this.filteredFoodItems.sort((a, b) => a.price - b.price);
    } else if (this.selectedSortOption === 'highToLow') {
      this.filteredFoodItems.sort((a, b) => b.price - a.price);
    }

    if (this.searchQuery) {
      this.filteredFoodItems = this.filteredFoodItems.filter(item =>
        item.name.toLowerCase().includes(this.searchQuery.toLowerCase())
      );
    }
  }
  scrollCategories(direction: string): void {
    const container = document.querySelector('.category-scroll-container');
    if (container) {
      const scrollAmount = direction === 'left' ? -150 : 150;
      container.scrollLeft += scrollAmount;
    }
  }


}
<nav class="navbar navbar-expand-lg navbar-light bg-warning">
  <ul class="navbar-nav category-scroll-container">
    <li class="nav-item" (click)="filterByCategory(null)">
      <a class="nav-link" [class.active]="selectedCategory === null">
        <i class="material-icons">all_inclusive</i> <span class="icon-text">All</span>
      </a>
    </li>
    <li class="nav-item" *ngFor="let category of categories" (click)="filterByCategory(category)">
      <a class="nav-link" [class.active]="selectedCategory === category">
        <i class="material-icons">fastfood</i> <span class="icon-text">{{ category.name }}</span>
      </a>
    </li>
  </ul>
</nav>


<div class="container-fluid">

  <div class="row">
    <div class="row mb-3">
      <div class="col-md-6">
        <!-- Search bar -->
        <div class="input-group">
          <input
            type="text"
            class="form-control"
            [(ngModel)]="searchQuery"
            placeholder="Search for food items"
            (input)="applyFilters()"
          />
          <button
            class="btn btn-outline-info"
            type="button"
            (click)="searchQuery = ''; applyFilters()"
          >
            Clear
          </button>
        </div>
      </div>
      <div class="col-md-3"></div>
      <div class="col-md-3">
        <!-- Filter select -->

        <select
          class="form-select"
          id="sortOption"
          [(ngModel)]="selectedSortOption"
          (change)="applyFilters()"
        >
          <optgroup label="Sort By">
            <option value="default"> Default</option>
            <option value="lowToHigh">Price: Low to High</option>
            <option value="highToLow">Price: High to Low</option>
          </optgroup>
        </select>
      </div>
    </div>

    <div
      class="col-md-4 mb-4"
      *ngFor="let foodItem of filteredFoodItems; let i = index"
    >
      <div class="card">
        <img
          src="{{ foodItem.imagePath }}"
          class="card-img-top card-img"
          alt="Food Image"
        />
        <div class="card-body">
          <h3 class="card-title" style="color: tomato">
            {{ foodItem.name }}
          </h3>
          <h4 class="card-title">{{ foodItem.category.name }}</h4>
          <p class="card-text">{{ foodItem.description }}</p>
          <p class="card-text">
            Price: {{ foodItem.price | currency : "INR" }}
          </p>
          <p class="card-text">
            Available Quantity: {{ foodItem.availableQuantity }}
          </p>
          <div style="margin: 0 auto; display: flex; align-items: center">
            <div class="input-group mr-2">
              <div class="input-group-prepend">
                <button
                  class="btn btn-outline-warning"
                  type="button"
                  (click)="decreaseQuantity(i)"
                  style="border-radius: 30%"
                >
                  -
                </button>
              </div>
              <input
                type="text"
                class="form-control text-center"
                style="max-width: 50px; border-radius: 30%"
                [(ngModel)]="quantities[i]"
                readonly
                min="1"
              />
              <div class="input-group-append">
                <button
                  class="btn btn-outline-success"
                  type="button"
                  (click)="increaseQuantity(i)"
                  style="border-radius: 30%"
                >
                  +
                </button>
              </div>
            </div>
            <button
              class="btn btn-success add-to-cart-button"
              (click)="addToCart(foodItem, quantities[i])"
            >
              Add to Cart
            </button>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
<div class="modal" id="successModal" tabindex="-1" role="dialog" aria-labelledby="successModalLabel" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="successModalLabel" style="color: green;">Item Added to Cart</h5>
        <button type="button" class="btn-close btn-danger" data-dismiss="modal" aria-label="Close" (click)="closeSuccessModal()">
        </button>
      </div>
      <div class="modal-body">
        <h6 style="color: blue;">The selected item has been successfully added to your cart.</h6>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-danger" data-dismiss="modal" (click)="closeSuccessModal()">Close</button>
      </div>
    </div>
  </div>
</div>
<div class="modal" id="alertModal" tabindex="-1" role="dialog" aria-labelledby="alertModalLabel" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="alertModalLabel" style="color: red;">Alert!</h5>
        <button type="button" class="btn-close btn-danger" data-dismiss="modal" aria-label="Close" (click)="closeAlertModal()">
        </button>
      </div>
      <div class="modal-body">
        <h6 style="color: black;">{{alertMessage}}</h6>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-danger" data-dismiss="modal" (click)="closeAlertModal()">Close</button>
      </div>
    </div>
  </div>
</div>
